fruits = ["تفاح", "موز", "برتقال"]

for fruit in fruits:
    print(fruit)

# من 0 إلى 4
for i in range(5):
    print(i)

# من 1 إلى 5
for i in range(1, 6):
    print(i)

# من 0 إلى 10 بخطوة 2
for i in range(0, 11, 2):
    print(i)  # 0, 2, 4, 6, 8, 10

name = "Python"

for char in name:
    print(char)

colors = ["أحمر", "أخضر", "أزرق"]

for index, color in enumerate(colors):
    print(f"{index}: {color}")
