for i in range(10):
    if i == 5:
        break
    print(i)
# يطبع: 0, 1, 2, 3, 4

for i in range(5):
    if i == 2:
        continue
    print(i)
# يطبع: 0, 1, 3, 4 (تخطى 2)

numbers = [1, 5, 8, 12, 15, 20]
target = 12

for num in numbers:
    if num == target:
        print(f"وجدنا {target}!")
        break
else:
    print("لم نجد العدد")
